import React, { useState } from 'react';
import ScratchPad from './ScratchPad';
import Slider from './Slider';
import Question from './Question';
import ScoreBoard from './ScoreBoard';
import CompetitionMode from './CompetitionMode';
import { generateRandomQuestion } from '../utils/mathUtils';
import '../App.css';

const App = () => {
  const [minValue, setMinValue] = useState(0);
  const [maxValue, setMaxValue] = useState(10);
  const [operation, setOperation] = useState('addition');
  const [score, setScore] = useState({ player1: 0, player2: 0 });
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [isCompetitionMode, setIsCompetitionMode] = useState(false);

  const handleSliderChange = (newValue) => {
    setMinValue(newValue);
    setMaxValue(newValue);
  };

  const handleOperationChange = (selectedOperation) => {
    setOperation(selectedOperation);
  };

  const startNewQuestion = () => {
    const { question, answer } = generateRandomQuestion(minValue, maxValue, operation);
    setCurrentQuestion({ question, answer });
  };

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setScore((prevScore) => ({
        ...prevScore,
        player1: prevScore.player1 + 1,
      }));
    }
    startNewQuestion();
  };

  return (
    <div>
      <h1>Math Game with Rainbows and Stars</h1>
      <Slider min={minValue} max={maxValue} onChange={handleSliderChange} />
      <Question operation={operation} min={minValue} max={maxValue} onAnswer={handleAnswer} />
      <ScratchPad />
      <ScoreBoard scores={score} />
      {isCompetitionMode && <CompetitionMode contestants={['Player 1', 'Player 2']} questionCount={10} onScoreUpdate={setScore} />}
      <button onClick={startNewQuestion}>Start New Question</button>
    </div>
  );
};

export default App;
